import React from 'react';
import * as sortingAlgorithm from '../sortingAlgorithms/sortingAlgorithms.js';
import './SortingVisualizer.css';

export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            array: [],
        };
    }

    componentDidMount() {
        this.resetArray();
    }

    resetArray() {
        const array = [];
        for (let i = 0; i < 310; i++) {
            array.push(randomIntFromInterval(5, 730));
        }
        this.setState({array});
    }

    mergeSort() {
        const animations = sortingAlgorithm.mergeSort(this.state.array);
        const newAnimations = [];
        for (const animation of animations) {
            newAnimations.push(animation.comparison);
            newAnimations.push(animation.comparison);
            newAnimations.push(animation.swap);
        }
        for (let i = 0; i < animations.length; i++) {
            const arrayBars = document.getElementsByClassName('array-bar');
            const [barOneIndex, barTwoIndex] = newAnimations[i];
            const barOneStyle = arrayBars[barOneIndex].style;
            const barTwoStyle = arrayBars[barTwoIndex].style;
            const isColorChange = i % 3 !== 2;
            if (isColorChange) {
                const color = i % 3 === 0 ? 'blue' : 'pink';
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                }, i + 5);
            } else {
                setTimeout(() => {
                    const tempHeight = barOneStyle.height;
                })
            }
            const {comparison, swap} = animations[i];
            setTimeout(() => {
                arrayBars[comparison[1]].style.backgroundColor = 'blue';
                arrayBars[comparison[0]].style.backgroundColor = 'blue';
                setTimeout (() => {
                    arrayBars[comparison[1]].style.backgroundColor = 'pink';
                    arrayBars[comparison[0]].style.backgroundColor = 'pink';
                }, (i + 1) * 10);
            }, i * 10);
        }
    }

    quickSort() {}

    heapSort() {}

    bubbleSort() {}

    render() {
        const {array} = this.state;
        return (
            <div className="array-container">
                {array.map((value, index) => (
                    <div className = "array-bar" key={index} style={{height: `${value}px`}}></div> 
                ))}
                <button onClick={() => this.resetArray()}>Generate New Array</button>
                <button onClick={() => this.mergeSort()}>Merge Sort</button>
                <button onClick={() => this.quickSort()}>Quick Sort</button>
                <button onClick={() => this.heapSort()}>Heap Sort</button>
                <button onClick={() => this.bubbleSort()}>Bubble Sort</button>
            </div>
        )
    }
}

function randomIntFromInterval(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
